targets.expr:
sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_usage{cluster="nexmark-flink", job_name=~"$job"}
  )
)

sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_capacity{cluster="nexmark-flink", job_name=~"$job"}
  )
)

sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_pinned_usage{cluster="nexmark-flink", job_name=~"$job"}
  )
)
